import { useState, useEffect } from "react";
import BottomHeader from "../Components/BottomHeader";
import Navbar from "../Components/Navbar";


function HomePage(){
    const [data, setData] = useState([]);

    const fetchData = (count: number) => {
        fetch(`http://127.0.0.1:8000/api/stock-sentiments/?count=${count}`)  // Use backticks for template literals
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                console.log(data.stock_sentiments);
                setData(data.stock_sentiments);
            })
            .catch(error => {
                console.error('There was a problem fetching the data:', error);
            });
    };

    useEffect(() => {
        // Get the text element after component is mounted
        const textElement: HTMLHeadingElement | null = document.getElementById("text") as HTMLHeadingElement;

        // Array of words to transform
        const words: string[] = [
            "Welcome",
            "to",
            "the",
            "future",
            "of",
            "trading",
            "Live",
            "better"
        ];

        // Function to animate text
        function animateText(index: number): void {
            // Update text content
            if (textElement) {
                textElement.textContent = words[index];
            }

            // Increment index
            index++;

            // Check if there are more words to animate
            if (index < words.length) {
                // Schedule next animation
                setTimeout(() => {
                    animateText(index);
                }, 500);
            }
        }

        // Start animation
        animateText(0);
    }, []);


    return(
        <div className="flex flex-col w-full h-screen custom-background-img-mobile sm:custom-background-img-desktop">
            <Navbar />
            <div id="home-container" className="flex flex-col items-center pt-10">
            <div id="animation-container">
                <h1>Welcome to the future of trading</h1>
                <h1 id="text">Live Better</h1>
            </div>
                <div id="button-container" className="pb-10">
                    <button className="bg-white hover:bg-white/80 active:bg-white text-black font-bold italic p-2 rounded-md shadow-lg" onClick={() => {fetchData(10)}}><p className=" rounded-lg shadow-lg">See the data</p></button>
                </div>
                <div id="text-container" className="w-5/6 sm:w-3/6">
                    <h1 className="font-bold italic">Analysis.</h1>
                    <h1 className="font-light text-justify pb-3 indent-12">...Enhance your trading edge with WSB Analytics, your premier destination for comprehensive insights into the WSB subreddit. Gain a competitive advantage by accessing data on promising stocks poised for significant gains. Instantly detect red flags for investments trending downwards, empowering informed decision-making for your portfolio.</h1>
                    <h1 className="font-bold italic">Visualization.</h1>
                    <h1 className="font-light text-justify indent-12">...Elevate your trading strategy with WSB Analytics, where intuitive graphs provide effortless data interpretation. Our cutting-edge tools harness the power of a state-of-the-art Language Model (LLM), finely tuned for unparalleled accuracy. Dive into detailed visual representations that streamline your analysis, allowing you to grasp complex market trends with ease. With WSB Analytics, stay ahead of the curve and make informed investment decisions backed by robust data and advanced technology.</h1>
                </div>
                <div>
                    <p>{data}</p>
                </div>
            </div>
            <BottomHeader />
        </div>
    )
}

export default HomePage
