import React, { useState } from 'react';
import Navbar from '../Components/Navbar';
import BottomHeader from '../Components/BottomHeader';

function DataPage() {
    const [iframeWidth, setIframeWidth] = useState(1000);
    const [iframeHeight, setIframeHeight] = useState(650);

    return (
        <div className="flex flex-col w-full h-screen items-center custom-background-img-mobile sm:custom-background-img-desktop">
            <Navbar />
            <div className="flex flex-col h-max w-full items-center overflow-y-auto gap-2 pt-8">
                <div>
                <h2 className="text-2xl font-bold">Training Data for Sentiment Analysis and Language Model Training</h2>
                <p className="font-light">
                The data being viewed, albeit not in the best format, is available in JSONL (JSON Lines) format on GitHub. This excerpt comprises 450 lines extracted from the main dataset utilized to train the Language Model (LLM).

                A portion of this dataset was employed to train the initial sentiment gathering process. This involved collecting sentiment labels for various texts. Subsequently, another segment of the data was utilized to train the Language Model to transform the sentiment analysis into a predictable and parsable string format. This dual-purpose training aimed to enhance both sentiment analysis accuracy and the language model's ability to generate coherent and structured textual output.

                Overall, this dataset serves as a foundational component in refining sentiment analysis techniques and advancing the capabilities of the Language Model through rigorous training and data-driven learning.</p>
                </div>
                <iframe 
                    src="https://drive.google.com/file/d/1ZOvHeQpRFOZz689fKGWIfavA5dwOo9ke/preview" 
                    title="PDF-Viewer" 
                    width={iframeWidth} 
                    height={iframeHeight} 
                    allow="autoplay"
                />
            </div>
            <BottomHeader />
        </div>
    );
}

export default DataPage;
