            "Live| Better",
            "Live| Better",
            "Live| Better",
            "Live| Better",
            "Liv| Better",
            "Li| Better",
            "L| Better",
            "| Better",
            "| Better",
            "T| Better",
            "Th| Better",
            "Thi| Better",
            "Thin| Better",
        ];


        // Function to animate text
        function animateText(index: number): void {
            // Update text content
            if (textElement) {
                textElement.textContent = words[index];
            }

            // Increment index
            index++;

            if (index >= words.length) {
                // Reset index to 0
                index = 0;
            }
            // Schedule next animation
            setTimeout(() => {
                animateText(index);
            }, 100);

        }

        // Start animation
        animateText(0);
    }, []);

    return(
        <div className="flex flex-col w-full h-screen custom-background-img-mobile sm:custom-background-img-desktop">
            <Navbar />
            <div id="home-container" className="flex flex-col items-center pt-36">
            <div id="animation-container" className="flex flex-col items-center sm:flex-row w-5/6">
                <div className="flex flex-col items-center w-4/6">
                    <div className="pb-8">
                        <h1 className="text-center items-center text-2xl pb-2">Welcome to the future of trading </h1> <h1 id="text" className="text-3xl text-center sm:text-7xl text-blue-500">Live Better</h1>
                    </div>
                    <div id="button-container" className=" text-3xl bg-gradient-to-r from-cyan-500 to-blue-500 hover:ring-1 hover:ring-white rounded-2xl shadow-lg shadow-blue-500/50">
                        <a className="text-white p-2" href="/analytics">See the data</a>
                    </div>
                </div>
                <div id="text-container" className="flex flex-col w-5/6 sm:w-3/6 justify-around">
                    <h1 className="pb-3 text-center text-4xl font-thin">Robust LLM fine-tuned on <span className="text-blue-500 text-5xl font-normal bg-gradient-to-r from-cyan-500 to-blue-500 text-transparent bg-clip-text">100k</span> lines of private curated data.</h1>
                    <h1 className="pb-3 text-center text-4xl font-thin">Analyzing <span className="text-blue-500 text-5xl font-normal bg-gradient-to-r from-cyan-500 to-blue-500 text-transparent bg-clip-text">thousands</span> of posts to boost your trading strategy.</h1>
                </div>
            </div>
            </div>
            <BottomHeader />
        </div>
    )
}

export default HomePage
